 // This sample code reads in image data from a RAW image file and // writes it into another file// NOTE:	The code assumes that the image is of size 256 x 256 and is in the//			RAW format. You will need to make corresponding changes to//			accommodate images of different sizes and/or types////////////////////////////////////////////// Name : SHILPASHREE RAO// USC ID: 5636765972// EMAIL ID: shilpasr@usc.edu// Submission Date: 2/4/2018// EE569 HW 1////////////////////////////////////////////////#include <stdio.h>#include <iostream>#include <stdlib.h>#include <math.h>using namespace std;int main(int argc, char *argv[]){	// Define file pointer and variables	FILE *f1;	FILE *f2;	FILE *f3;	FILE *file;	int BytesPerPixel;	int Size = 256;		// Check for proper syntax	if (argc < 2){		cout << "Syntax Error - Incorrect Parameter Usage:" << endl;		//cout << "program_name input_image.raw output_image.raw [BytesPerPixel = 1] [Size = 256]" << endl;		cout << "program_name input_image.raw [BytesPerPixel = 1] [Size = 256]" << endl;		return 0;	}		// Check if image is grayscale or color	if (argc < 3){		BytesPerPixel = 1; // default is grey image	} 	else {		BytesPerPixel = atoi(argv[2]);		// Check if size is specified		if (argc >= 4){			Size = atoi(argv[3]);		}	}	// Allocate image data array	unsigned char Imagedata[Size][Size][BytesPerPixel];	// Read image (filename specified by first argument) into image data matrix	if (!(file=fopen(argv[1],"rb"))) {		cout << "Cannot open file: " << argv[1] <<endl;		exit(1);	}	fread(Imagedata, sizeof(unsigned char), Size*Size*BytesPerPixel, file);	fclose(file);	///////////////////////// INSERT YOUR PROCESSING CODE HERE /////////////////////////		int TargetSize = 650;	unsigned char bilinearOutput[TargetSize][TargetSize][BytesPerPixel];	float xEstimate, yEstimate, xOffset, yOffset;	float xRatio = (static_cast<float>(Size))/TargetSize;	float yRatio = (static_cast<float>(Size))/TargetSize;	int xRounded, yRounded;	for(int i = 0; i < TargetSize; i++){		for(int j = 0; j < TargetSize; j++){			xEstimate = i * xRatio;			xRounded = static_cast<int>(xEstimate);			xOffset = xEstimate - static_cast<float>(xRounded);			yEstimate = j * yRatio;			yRounded = static_cast<int>(yEstimate);			yOffset = yEstimate - static_cast<float>(yRounded);			for(int k = 0; k < 3; k++){				bilinearOutput[i][j][k] = Imagedata[xRounded][yRounded][k] * (1 - xOffset) * (1 - yOffset) + 					Imagedata[xRounded][yRounded+1][k] * (1 - xOffset) * yOffset + 					Imagedata[xRounded+1][yRounded][k] * xOffset * (1 - yOffset) + 					Imagedata[xRounded+1][yRounded+1][k] * xOffset * yOffset;			}		}	}	// Write image data (filename specified by second argument) from image data matrix	//if (!(file=fopen(argv[2],"wb"))) {	//	cout << "Cannot open file: " << argv[2] << endl;	//	exit(1);	///}	f1 = fopen("bilinear_output.raw","wb");	fwrite(bilinearOutput, sizeof(unsigned char), TargetSize*TargetSize*BytesPerPixel, f1);	fclose(f1);	return 0;}